import React from "react";
import Header from "./component/Header";
import Todos from "./component/Todos";
import { useState, useEffect } from "react";
function App() {
  const [todos, setTodos] = useState([]);

  // I have added this state so that I can send it to Todos component to grab/get "todos.length"
  const[todoLength, setTodoLength] = useState(1);

  // Add Todo
  const addTodo = (todo) => {
    const id = Math.floor(Math.random() * 1000) + 1;
    const newTodo = { id, ...todo };
    setTodos([...todos, newTodo]);
    console.log(todo);
    console.log(todos.length)
  };

  useEffect(() => {
    setTodoLength(todos.length)
  console.log(todoLength + todos.length);
  },[])

  // Delete Todo
  const deleteTodo = (id) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  // Toggle remainder
  const toggleTodo = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, isCompleted: !todo.isCompleted } : todo
      )
    );
  };

  return (
    <div className="todo-container">
      <Header onAdd={addTodo} />
      {todos.length > 0 ? (
        <Todos todos={todos} onDelete={deleteTodo} onToggle={toggleTodo} /> // I have added getTodosLength to Todos component so the function will be used to capture todos length from Todos component.
      ) : (
        "No todos to show!"
      )}
    </div>
  );
}

export default App;
